import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import SignUp from './SignUp';
import SignIn from './SignIn';  // Assuming you have a SignIn component ready
import './App.css';

const App = () => {
  const [isNewUser, setIsNewUser] = useState(false);

  useEffect(() => {
    // Check if the user is a first-time user
    const userData = localStorage.getItem('user');
    if (userData) {
      setIsNewUser(false);  // If data exists, assume the user is a returning user
    } else {
      setIsNewUser(true);  // No user data found, assume it's a first-time user
    }
  }, []);

  return (
    <Router>
      <div className="app">
        <h1>Welcome to SmartNutrition</h1>

        {isNewUser ? (
          <div>
            <Link to="/signup">
              <button>Sign Up</button>
            </Link>
            <Link to="/signin">
              <button>Sign In</button>
            </Link>
          </div>
        ) : (
          <div>
            {/* Render some home page or dashboard for returning users */}
            <h2>Welcome back!</h2>
            <Link to="/dashboard">
              <button>Go to Dashboard</button>
            </Link>
          </div>
        )}

        {/* Update this part to use Routes instead of Switch */}
        <Routes>
          <Route path="/signup" element={<SignUp />} />
          <Route path="/signin" element={<SignIn />} />
          {/* Add other routes for your app */}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
