import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import SignUp from './SignUp';
import SignIn from './SignIn';
import './App.css';

const App = () => {
  const [isNewUser, setIsNewUser] = useState(false);
  const [showWelcome, setShowWelcome] = useState(true);  // State to manage visibility of the welcome message
  const navigate = useNavigate();

  useEffect(() => {
    // Check if the user is a first-time user
    const userData = localStorage.getItem('user');
    if (userData) {
      setIsNewUser(false);  // If data exists, assume the user is a returning user
    } else {
      setIsNewUser(true);  // No user data found, assume it's a first-time user
    }
  }, []);

  const handleNavigation = (path) => {
    setShowWelcome(false);  // Hide welcome message and buttons
    navigate(path);  // Navigate to the Sign Up or Sign In page
  };

  return (
    <Router>
      <div className="app">
        {showWelcome && (
          <div className="welcome-container">
            <h1>Welcome to SmartNutrition</h1>
            <div className="buttons-container">
              <button onClick={() => handleNavigation('/signup')}>Sign Up</button>
              <button onClick={() => handleNavigation('/signin')}>Sign In</button>
            </div>
          </div>
        )}

        <Routes>
          <Route path="/signup" element={<SignUp />} />
          <Route path="/signin" element={<SignIn />} />
          {/* Add other routes for your app */}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
