import React, { useState } from 'react';
import { Route, Routes, useNavigate } from 'react-router-dom';
import SignUp from './SignUp';
import SignIn from './SignIn';
import './App.css';

const App = () => {
  const [showWelcome, setShowWelcome] = useState(true);
  const navigate = useNavigate();

  const handleNavigation = (path) => {
    setShowWelcome(false); // Hide the welcome message after clicking a button
    navigate(path); // Navigate to the Sign Up or Sign In page
  };

  return (
    <div className="app">
      {/* Render Welcome screen only on the home route */}
      {showWelcome && (
        <div className="welcome-container">
          <h1>Welcome to SmartNutrition</h1>
          <div className="buttons-container">
            <button onClick={() => handleNavigation('/signup')}>Sign Up</button>
            <button onClick={() => handleNavigation('/signin')}>Sign In</button>
          </div>
        </div>
      )}

      <Routes>
        <Route path="/" element={<Welcome />} />
        <Route path="/signup" element={<SignUp navigate={navigate} />} />
        <Route path="/signin" element={<SignIn navigate={navigate} />} />
      </Routes>
    </div>
  );
};

// Welcome page is only shown when on the '/' route
const Welcome = () => {
  return (
    <div className="welcome-container">
      <h1>Welcome to SmartNutrition</h1>
      <div className="buttons-container">
        <button onClick={() => window.location.href = '/signup'}>Sign Up</button>
        <button onClick={() => window.location.href = '/signin'}>Sign In</button>
      </div>
    </div>
  );
};

export default App;
